CREATE DATABASE platziblog DEFAULT CHARACTER SET utf8;
USE platziblog;

-- CREAR TABLAS ---
CREATE TABLE people (
person_id INT PRIMARY KEY AUTO_INCREMENT,
last_name VARCHAR(255) NULL,
first_name VARCHAR(255) NULL,
address VARCHAR(255) NULL,
city VARCHAR(255) NULL
);


CREATE TABLE categorias(
id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
nombre_categoria VARCHAR(30) NOT NULL
);

CREATE TABLE etiquetas(
id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
nombre_etiqueta VARCHAR(30) NOT NULL
);


CREATE TABLE usuarios(
id INT PRIMARY KEY NOT NULL AUTO_INCREMENT,
login VARCHAR(30) NOT NULL,
password VARCHAR(32) NOT NULL,
nickname VARCHAR(40) NOT NULL,
email VARCHAR(40) UNIQUE NOT NULL
);



CREATE TABLE `platziblog`.`posts` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `titulo` VARCHAR(130) NOT NULL,
  `fecha_publicacion` TIMESTAMP NULL,
  `contenido` TEXT NOT NULL,
  `estatus` CHAR(8) NULL DEFAULT 'activo',
  `usuario_id` INT NOT NULL,
  `categoria_id` INT NOT NULL,
  PRIMARY KEY (`id`),
  INDEX `posts_usuarios_idx` (`usuario_id` ASC),
  INDEX `posts_categorias_idx` (`categoria_id` ASC),
  CONSTRAINT `posts_usuarios`
    FOREIGN KEY (`usuario_id`)
    REFERENCES `platziblog`.`usuarios` (`id`)
    ON DELETE NO ACTION
    ON UPDATE CASCADE,
  CONSTRAINT `posts_categorias`
    FOREIGN KEY (`categoria_id`)
    REFERENCES `platziblog`.`categorias` (`id`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION);



CREATE TABLE `platziblog`.`comentarios` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `cuerpo_comentario` TEXT NOT NULL,
  `usuario_id` INT NOT NULL,
  `post_id` INT NOT NULL,
  PRIMARY KEY (`id`),
  INDEX `comentarios_usuarios_idx` (`usuario_id` ASC),
  INDEX `comentarios_post_idx` (`post_id` ASC),
  CONSTRAINT `comentarios_usuarios`
    FOREIGN KEY (`usuario_id`)
    REFERENCES `platziblog`.`usuarios` (`id`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION,
  CONSTRAINT `comentarios_post`
    FOREIGN KEY (`post_id`)
    REFERENCES `platziblog`.`post` (`id`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION);



CREATE TABLE `platziblog`.`posts_etiquetas` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `post_id` INT NOT NULL,
  `etiqueta_id` INT NOT NULL,
  PRIMARY KEY (`id`),
  INDEX `post_etiquetas_idx` (`post_id` ASC),
  INDEX `postetiquetas_etiquetas_idx` (`etiqueta_id` ASC),
  CONSTRAINT `poststiquetas_post`
    FOREIGN KEY (`post_id`)
    REFERENCES `platziblog`.`post` (`id`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION,
  CONSTRAINT `postetiquetas_etiquetas`
    FOREIGN KEY (`etiqueta_id`)
    REFERENCES `platziblog`.`etiquets` (`id`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION);




