-- Saber el total de registros que que hay en la tabla Posts
SELECT COUNT(*) AS numero_post
FROM posts;

-- El Left join lo que hace es traer los usuarios a un que no tengan posts (null)
SELECT *
FROM  usuarios
	LEFT JOIN posts ON usuarios.id = posts.usuario_id
WHERE posts.usuario_id IS NULL;
 
-- El Right join lo que hace es traer todos los posts a un que no esten asociados a un usuario
SELECT *
FROM  usuarios
	RIGHT JOIN posts ON usuarios.id = posts.usuario_id
WHERE posts.usuario_id is NULL;

-- El Inner join trae los datos ligados de las 2 tablas donde no son nulos
SELECT *
FROM  usuarios
		INNER JOIN posts ON usuarios.id = posts.usuario_id;

-- Ejemplo de OUTER JOIN para traer todos los usuarios que tienen algun campo nulo en alguno de los lados
SELECT *
FROM  usuarios
	LEFT JOIN posts ON usuarios.id = posts.usuario_id
UNION
SELECT *
FROM  usuarios
		RIGHT JOIN posts ON usuarios.id = posts.usuario_id;

-- Contrario del outer Join para devolver solo los que tienen campos nulos
 SELECT *
FROM  usuarios
	LEFT JOIN posts ON usuarios.id = posts.usuario_id
WHERE posts.usuario_id is NULL
UNION
SELECT *
FROM  usuarios
		RIGHT JOIN posts ON usuarios.id = posts.usuario_id   
WHERE posts.usuario_id IS NULL;


