-- Ejemplos de GROUP BY
SELECT estatus, COUNT(*)post_quantity
FROM posts
GROUP BY estatus;

--
SELECT estatus, COUNT(id)suma_id
FROM posts
GROUP BY estatus;

-- Saber cuantos posts se hicieron cada año
SELECT YEAR(fecha_publicacion) AS post_year, COUNT(*) AS post_quantity
FROM posts
GROUP BY post_year;

-- Saber cuantos posts se hicieron por mes
SELECT MONTHNAME(fecha_publicacion) AS post_month, COUNT(*) AS post_quantity
FROM posts
GROUP BY post_month;


-- Al a grupar por estatus y mes nos dice cuantos hay activos y inactivos en cada mes
SELECT estatus, MONTHNAME(fecha_publicacion) AS post_month, COUNT(*) AS post_quantity
FROM posts
GROUP BY estatus, post_month;


-- Ordenar por fecha de publicacion y de manera ascendente
SELECT * 
FROM posts
ORDER BY fecha_publicacion ASC;

-- Ordenar por fecha de publicacion y de manera descendente
SELECT * 
FROM posts
ORDER BY fecha_publicacion DESC;


-- Treae los 5 primeros post ordenados por fecha de publicacion descendiente
SELECT * 
FROM posts
ORDER BY fecha_publicacion DESC
LIMIT 5;

--
SELECT MONTHNAME(fecha_publicacion) AS post_month , estatus, COUNT(*) AS post_quantity
FROM posts
GROUP BY estatus, post_month
HAVING post_quantity > 2
ORDER BY post_month;




-- Ejemplo de nested query
-- se crea la tabla new_table projection como subconsulta
SELECT new_table_projection.date, COUNT(*) AS posts_count
FROM (
SELECT DATE(MIN(fecha_publicacion)) AS date, YEAR(fecha_publicacion) AS post_year
FROM posts
GROUP BY post_year
)AS new_table_projection
GROUP BY new_table_projection.date
ORDER BY new_table_projection.date;


-- Ejemplo de subconsulta para traer el post con la fecha de publicacion mas alta
SELECT *
FROM posts
WHERE fecha_publicacion = (
 SELECT MAX(fecha_publicacion)
 FROM posts
);



